import { Methods, request } from '@/libs/request'
import { ROUTER } from '@/router'
import { getAccessToken } from '@/utils/jwt'

export type postApproveTeahouseType = {
  approvementType: 'REJECT' | 'APPROVE';
  id: number;
}

const { TEAHOUSE } = ROUTER

export const postApproveTeahouse = async({ approvementType, id }: postApproveTeahouseType) => {
  return request<{ id: number }>({
    url: `${TEAHOUSE}/${id}/approve`,
    method: Methods.POST,
    data: { approvementType },
    headers: { Authorization: `Bearer ${getAccessToken()}` }
  })
}
