import { Box, Button, TextField, Typography } from '@mui/material'
import { useState } from 'react'
import { useParams } from 'react-router-dom'

import { PostCreateProductType } from '@/apis/shopping/postCreateProduct'
import usePostCreateProduct from '@/hooks/shopping/usePostCreateProduct'

const ProductCreatePage = () => {
  const { mutateAsync: create } = usePostCreateProduct()
  const { id } = useParams()

  const [productData, setProductData] = useState<PostCreateProductType>({
    userId: 1,
    shopId: Number(id),
    name: '',
    price: 0,
    summary: '',
    description: '',
    imageIds: [],
    detailImageIds: []
  })

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target
    setProductData({ ...productData, [name]: value })
  }

  const handleCreate = async () => {
    try {
      await create(productData)
      // Optionally: Redirect or show success message
    } catch (error) {
      console.error('Error creating product', error)
    }
  }

  return (
    <Box sx={{ padding: '20px', display: 'flex', flexDirection: 'column', gap: '15px' }}>
      <Typography variant="h4">Create Product</Typography>

      <TextField
        label="User ID"
        type="number"
        name="userId"
        value={productData.userId}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Shop ID"
        type="number"
        name="shopId"
        value={productData.shopId}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Product Name"
        name="name"
        value={productData.name}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Price"
        type="number"
        name="price"
        value={productData.price}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Summary"
        name="summary"
        value={productData.summary}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Description"
        name="description"
        value={productData.description}
        onChange={handleChange}
        multiline
        rows={4}
        fullWidth
      />

      <TextField
        label="Image IDs (comma separated)"
        name="imageIds"
        value={productData.imageIds.join(',')}
        onChange={(e) => setProductData({ ...productData, imageIds: e.target.value.split(',').map(Number) })}
        fullWidth
      />

      <TextField
        label="Detail Image IDs (comma separated)"
        name="detailImageIds"
        value={productData.detailImageIds.join(',')}
        onChange={(e) => setProductData({ ...productData, detailImageIds: e.target.value.split(',').map(Number) })}
        fullWidth
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleCreate}>
        Create Product
      </Button>
    </Box>
  )
}

export default ProductCreatePage
