import { Button, CircularProgress } from '@mui/material'
import { useNavigate } from 'react-router-dom'

import { PostItemModel } from '@/apis/post/getPosts'
import usePosts from '@/hooks/post/usePosts'

const PostMainPage = () => {
  const { data: posts, isLoading } = usePosts()
  const navigate = useNavigate()
  return (
    <div>
      <div style={{ display: 'flex', flexDirection: 'row' }}>
        <h1>글/포스트 관리</h1>
        <Button onClick={() => navigate('filter')}>필터</Button>
      </div>

      <h3>현재까지 작성된 글</h3>
      {isLoading ? <CircularProgress /> : posts?.map((post) => (<PostItem
        key={post.id}
        post={post} />)) }
    </div>
  )
}
export default PostMainPage

type PostItemProps = {
  post: PostItemModel;
}
const PostItem = ({ post }: PostItemProps) => {
  return (
    <div style={{ display: 'flex', flexDirection: 'row', gap: '10px' }}>
      <div>{post.id}</div>
      <div>
        <b>{post.title}</b>
        <div>{post.content}</div>
      </div>
      <div style={{ width: 50, height: 50, display: 'flex', flexDirection: 'row', gap: 10 }}>
        {post.images.map((image) => (<img
          key={image}
          src={image} />))}
      </div>
    </div>
  )
}
