import { Box, Button, TextField, Typography } from '@mui/material'
import { useEffect, useRef, useState } from 'react'
import { useParams } from 'react-router-dom'
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd'

import { PostCreateProductType } from '@/apis/shopping/postCreateProduct'
import usePostCreateProduct from '@/hooks/shopping/usePostCreateProduct'
import usePostUploadFiles from '@/hooks/upload/useUploadFiles'

const ProductCreatePage = () => {
  const { mutateAsync: create } = usePostCreateProduct()
  const { mutateAsync: upload } = usePostUploadFiles()
  const { id } = useParams()

  const fileInputRef = useRef<HTMLInputElement>(null)
  const detailFileInputRef = useRef<HTMLInputElement>(null)

  const [selectedFiles, setSelectedFiles] = useState<FileList | null>(null)
  const [selectedDetailFiles, setSelectedDetailFiles] = useState<FileList | null>(null)

  const [imagePreviews, setImagePreviews] = useState<string[]>([])
  const [detailImagePreviews, setDetailImagePreviews] = useState<string[]>([])

  const [uploadedImageIds, setUploadedImageIds] = useState<number[]>([])
  const [uploadedDetailImageIds, setUploadedDetailImageIds] = useState<number[]>([])

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>, type: 'normal' | 'detail') => {
    const files = event.target.files
    if (files) {
      const fileURLs = Array.from(files).map((file) => URL.createObjectURL(file))
      if (type === 'normal') {
        setSelectedFiles(files)
        setImagePreviews(fileURLs)
      } else {
        setSelectedDetailFiles(files)
        setDetailImagePreviews(fileURLs)
      }
    }
  }

  useEffect(() => {
    return () => {
      imagePreviews.forEach((url) => URL.revokeObjectURL(url))
      detailImagePreviews.forEach((url) => URL.revokeObjectURL(url))
    }
  }, [imagePreviews, detailImagePreviews])

  // 이미지 업로드 함수 (일반 이미지용)
  const uploadImages = async (files: FileList | null): Promise<number[] | undefined> => {
    if (files) {
      try {
        const filesArray = Array.from(files)
        const data = await upload(filesArray)
        alert('이미지 업로드 완료')
        return data.images.map((image: { id: number }) => image.id)
      } catch (error) {
        alert('이미지 업로드 중 에러가 발생했습니다.')
      }
    }
    return undefined
  }

  // 상품 정보 상태 관리
  const [productData, setProductData] = useState<PostCreateProductType>({
    userId: 1,
    shopId: Number(id),
    name: '',
    price: 0,
    summary: '',
    description: '',
    imageIds: [],
    detailImageIds: []
  })

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target
    setProductData({ ...productData, [name]: value })
  }

  // 상품 생성 처리
  const handleCreate = async () => {
    try {
      // 일반 이미지 업로드
      const imageIds = await uploadImages(selectedFiles)
      // 세부 이미지 업로드
      const detailImageIds = await uploadImages(selectedDetailFiles)

      if (imageIds && detailImageIds) {
        // 업로드된 이미지 ID를 상태에 반영
        setProductData((prevData) => ({
          ...prevData,
          imageIds,
          detailImageIds
        }))

        // 상품 생성 API 호출
        await create({
          ...productData,
          imageIds,
          detailImageIds
        })

        alert('상품이 성공적으로 생성되었습니다.')
      }
    } catch (error) {
      console.error('Error creating product', error)
      alert('상품 생성 중 에러가 발생했습니다.')
    }
  }

  // 드래그 앤 드롭 이벤트 처리
  const handleOnDragEnd = (result: any) => {
    if (!result.destination) return

    const items = Array.from(imagePreviews)
    const [reorderedItem] = items.splice(result.source.index, 1)
    items.splice(result.destination.index, 0, reorderedItem)

    setImagePreviews(items)
  }

  return (
    <Box sx={{ padding: '20px', display: 'flex', flexDirection: 'column', gap: '15px' }}>
      <Typography variant="h4">Create Product</Typography>

      <TextField
        label="User ID"
        type="number"
        name="userId"
        value={productData.userId}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Shop ID"
        type="number"
        name="shopId"
        value={productData.shopId}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Product Name"
        name="name"
        value={productData.name}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Price"
        type="number"
        name="price"
        value={productData.price}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Summary"
        name="summary"
        value={productData.summary}
        onChange={handleChange}
        fullWidth
      />

      <TextField
        label="Description"
        name="description"
        value={productData.description}
        onChange={handleChange}
        multiline
        rows={4}
        fullWidth
      />

      {/* 일반 이미지 파일 업로드 */}
      <Typography variant="h6">일반 이미지 업로드</Typography>
      <input
        id="imageInput"
        type="file"
        onChange={(e) => handleFileChange(e, 'normal')}
        multiple
        ref={fileInputRef}
      />

      {/* 이미지 순서를 드래그 앤 드롭으로 변경 */}
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="images">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {imagePreviews.map((src, index) => (
                <Draggable key={src} draggableId={src} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        margin: '0 0 8px 0',
                        ...provided.draggableProps.style
                      }}
                    >
                      <img src={src} alt={`preview-${index}`} width="100px" />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {/* 세부 이미지 파일 업로드 */}
      <Typography variant="h6">세부 이미지 업로드</Typography>
      <input
        id="detailImageInput"
        type="file"
        onChange={(e) => handleFileChange(e, 'detail')}
        multiple
        ref={detailFileInputRef}
      />

      {detailImagePreviews.length > 0 && (
        <div>
          {detailImagePreviews.map((src, index) => (
            <img key={index} src={src} alt={`detail-preview-${index}`} width="100px" />
          ))}
        </div>
      )}

      <Button
        variant="contained"
        color="primary"
        onClick={handleCreate}
      >
        Create Product
      </Button>
    </Box>
  )
}

export default ProductCreatePage
