import { Box, Card, CardContent, CircularProgress, Typography } from '@mui/material'
import { useParams } from 'react-router-dom'

import useShop from '@/hooks/shopping/useShop'

const ShopDetailPage = () => {
  const { id } = useParams()
  const { data: shop, isLoading } = useShop(Number(id))

  if (isLoading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    )
  }

  if (!shop) {
    return (
      <Box sx={{ padding: '20px', textAlign: 'center' }}>
        <Typography variant="h5">Shop not found</Typography>
      </Box>
    )
  }

  return (
    <Box sx={{ padding: '20px' }}>
      <Typography
        variant="h4"
        gutterBottom>
        Shop Details
      </Typography>

      <Card sx={{ minWidth: 275 }}>
        <CardContent>
          <Typography
            variant="h5"
            component="div">
            {shop.name}
          </Typography>
          <Typography
            color="textSecondary"
            sx={{ marginBottom: '12px' }}>
            Created at: {new Date(shop.createdAt).toLocaleDateString()}
          </Typography>
          <Typography
            variant="body2"
            color="textSecondary">
            Owner: {shop.owner.nickname} ({shop.owner.handle})
          </Typography>
          <Typography
            variant="body2"
            color="textSecondary">
            Phone Number: {shop.owner.phoneNumber}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  )
}

export default ShopDetailPage
