import styled from '@emotion/styled'

import useTeahouses from '@/hooks/teahouse/useTeahouses'
import timeSince from '@/libs/timeSince'
import usePostApproveTeahouse from '@/hooks/teahouse/usePostApproveTeahouse'

const TeahouseAdminPage = () => {
  const { data: teahouses } = useTeahouses()
  const { mutate: approveTeahouse } = usePostApproveTeahouse()
  const sortedTeahouses = teahouses?.sort((a, b) => {
    return new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
  })
  function approveHandler(id: number) {
    approveTeahouse({ approvementType: 'APPROVE', id })
  }
  function rejectHandler(id: number) {
    approveTeahouse({ approvementType: 'REJECT', id })
  }
  return (
    <PageWrapper>
      <h1>찻집, 공간 승인 관련</h1>
      {sortedTeahouses?.map((teahouse, index) => {
        return (
          <TeahouseWrapper key={index}>
            <span>{teahouse.id}</span>
            <TeahouseApprovementBadge approved={teahouse.approved} />
            <span>{timeSince(teahouse.createdAt)}</span>
            <span>{teahouse.nation}</span>
            <span><b>{teahouse.name}</b></span>
            <span>{teahouse.location}</span>
            <button onClick={() => approveHandler(teahouse.id)}>승인하기</button>
            <button onClick={() => rejectHandler(teahouse.id)}>반려하기</button>
            <button>반려하기</button>
          </TeahouseWrapper>
        )
      })}
    </PageWrapper>
  )
}
export default TeahouseAdminPage

const PageWrapper = styled.div`
  padding: 60px 20px 20px 20px;
  display: flex;
  flex-direction: column;
  gap: 10px;
`
const TeahouseWrapper = styled.div`
  display: flex;
  flex-direction: row;
  gap: 5px;
`

const TeahouseApprovementBadgeWrapper = styled.div<{ approved: boolean }>`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 3px 10px;
  border-radius: 5px;
  font-size: 12px;
  background-color: ${({ approved }) => approved ? 'green' : 'yellow'}; 
`
type TeahouseApprovementBadgeType = { approved: boolean }
const TeahouseApprovementBadge = ({ approved }: TeahouseApprovementBadgeType) => {
  return (
    <TeahouseApprovementBadgeWrapper approved={approved}>
      {approved ? '승인 완료' : '미승인'}
    </TeahouseApprovementBadgeWrapper>
  )
}
